import { DefaultMantineColor } from "@mantine/core";

export type ServiceStat = {
  label: string;
  count: string;
  color: DefaultMantineColor;
};

export enum PlanFrequency {
  MONTHLY = "monthly",
  YEARLY = "yearly",
}

export type FrequencyOption = {
  label: string;
  value: PlanFrequency;
};

export type PlanOffer = {
  symbol: string;
  ccy: string;
  price: number;
  frequencyOption: FrequencyOption;
};

export type PlanTier = {
  id: number;
  accountCount: number;
  name: string;
  offers: PlanOffer[];
};

export type ServiceData = {
  slug: string;
  subscribed?: boolean; // receive update on room change
  image: string;
  iconImage: string;
  title: string;
  description: string;
  stat: {
    queue: number;
    room: number;
    matched: number;
  };
  planTeirs: PlanTier[];

  tags: string[];
};

